if (msg.payload.fields.hasOwnProperty("pm25")) { 
    if (Number(msg.payload.fields.pm25)>context.get("maxPM25")) {
        context.set("maxPM25", Number(msg.payload.fields.pm25));
    }
}

if (msg.payload.fields.hasOwnProperty("pm10")) { 
    if (Number(msg.payload.fields.pm10)>context.get("maxPM10")) {
        context.set("maxPM10", Number(msg.payload.fields.pm10));
    }
}

if (msg.payload.fields.hasOwnProperty("temperature")) { 
    if (Number(msg.payload.fields.temperature)>context.get("maxTemperature")) {
        context.set("maxTemperature", Number(msg.payload.fields.temperature));
    }
}

if (msg.payload.fields.hasOwnProperty("humidity")) { 
    if (Number(msg.payload.fields.humidity)>context.get("maxHumidity")) {
        context.set("maxHumidity", Number(msg.payload.fields.humidity));
    }
}

let newPayload = {
    maxTemperature: context.get("maxTemperature"),
    maxHumidity: context.get("maxHumidity"),
    maxPM25: context.get("maxPM25"),
    maxPM10: context.get("maxPM10"),
};


let newMsg = {
    topic: msg.topic,
    payload: newPayload,
};

return newMsg;

